                //     },config.JWT_SECRET,
                //     {
                //         expiresIn:'1h'
                //     }
                //     )
                    return res.status(200).json({
                        message: 'authentication successful',
                        
                    })
                }
                res.status(401).json({
                    message:'wrong email or password'
                }) ;
            })
        })
        .catch()
    })

module.exports = router;

